import React from 'react';
import Carousel from 'react-material-ui-carousel';
import { Paper, Button } from '@material-ui/core';
import { projects } from '../../data';
import { styled } from '@material-ui/core/styles';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExternalLinkAlt } from '@fortawesome/free-solid-svg-icons';

export const MyCar = styled(Carousel)({
    backgroundColor: "#2B2F32",
    height: "80vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    alignContent: "center",
    color: "#51595F",
    borderRadius: "5px",
    overflow: "hidden",

});
export const MyPaper = styled(Paper)({
    // display: "flex",
    // flexDirection: "column",
    height: "90%",
    width: "90%",
    backgroundColor: "#F4F4F4",
    overflow: "hidden"
});



const Pros = ({ isOpen }) => {

    const card = (props) => {
        return (
            <MyPaper style={{ textAlign: "left", overflow: "hidden", padding: "1rem" }}>
                <div style={{fontWeight: "bold"}}>{props.name}</div>
                <div>{props.subtitle}</div>
                <div style={{ display: "flex", justifyContent: "space-evenly", alignItems: "center"}}>
                    <img style={{ width: "55%", height: "100%"}} src={projects[0].photos[0]} />
                    <div style={{display: "flex", flexDirection: "column", width: "40%"}}>
                        <div className='project-description'>Role: {projects[0].role}</div>
                        <p>{props.description}</p>
                        <div style={{display: "flex", justifyContent: "space-evenly"}}>
                            <div className='vd-button' ><a className='visit directly' style={{ color: '#004DC0', textDecoration: 'none' }} target="_blank" href={projects[0].URL[0]}><FontAwesomeIcon icon={faExternalLinkAlt} /> Visit Directly</a></div>
                            <div className='gh-button' ><a className='visit github' style={{ color: 'white', textDecoration: 'none' }} target="_blank" href={projects[0].URL[1]}><FontAwesomeIcon icon={faExternalLinkAlt} /> Visit Github</a></div>
                        </div>
                    </div>
                </div>
                <section className='tech-section' style={{paddingBottom: "3rem"}}>
                    <h4 className='techHeading'>Technologies used:</h4>
                    <div style={{ display: 'flex' }}>
                        {projects[0].tech.map(tech => (<div key={tech} className='tech'>{tech}</div>))}
                    </div>
                </section>
            </MyPaper>
        )
    }
    return (
        <>
            {isOpen ?
                <div syle={{ position: 'absolute', display: "flex", justifyContent: "space-around" }}>
                    <MyCar
                        autoPlay={false}
                        className="car"
                        animation="slide"
                        classes={{}}
                        next={(next, active) => console.log(`we left ${active}, and are now at ${next}`)}
                        prev={(prev, active) => console.log(`we left ${active}, and are now at ${prev}`)}
                    >
                        {projects.map(project => card(project))}
                    </MyCar>
                </div>
                :
                null
            }
        </>
    )
}

export default Pros
