import React, { useState } from 'react';
import Modal from 'react-modal';
import { styled } from '@material-ui/core/styles';
import { projects } from '../../data';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowCircleRight, faArrowCircleLeft, faTimesCircle, faExternalLinkAlt } from '@fortawesome/free-solid-svg-icons';
import Button from '@material-ui/core/Button';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        height: "85vh",
        width: "80vw",
        overflowX: "hidden",
        display: "flex",
        flexDirection: "column"
    }
};

const Exit = styled(Button)({
    width: "5vw"
});

const ProjectButtons = styled(Button)({

})


const Pros = ({ setIsOpen, modalIsOpen }) => {
    const [myProjects, setProjects] = useState(projects)
    const [currentIndex, setCurrentIndex] = useState(0)
    const closeModal = () => {
        setIsOpen(false);
    }

    console.log(myProjects)
    return (
        <div>

            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                style={customStyles}

            >
                <div style={{ display: "flex", justifyContent: "flex-end", padding: '.5rem' }}>
                    <Exit color="secondary" style={{}} onClick={closeModal}><FontAwesomeIcon className="fas fa-camera fa-2x" icon={faTimesCircle} /></Exit>
                </div>
                <div style={{ height: "65vh", padding: "1rem", backgroundColor: "#F9F9F9", borderRadius: "5px", display: "flex" }}>
                    <div style={{ padding: "1rem" }}>
                        <div style={{ fontWeight: "bold", paddingBottom: ".5rem"}}>{myProjects[currentIndex].name}</div>
                        <div style={{paddingBottom: ".7rem"}}>{myProjects[currentIndex].subtitle}</div>
                        <img style={{ width: "600px", height: "350px", boxShadow:  "0px 10px 20px rgba(0, 0, 0, 0.12)", borderRadius: ".3rem" }} src={myProjects[currentIndex].photos[0]} />
                        <section className='tech-section' style={{ paddingBottom: "3rem" }}>
                            <h4 className='techHeading'>Technologies used:</h4>
                            <div style={{ display: 'flex' }}>
                                {projects[currentIndex].tech.map(tech => (<div key={tech} className='tech'>{tech}</div>))}
                            </div>
                        </section>
                    </div>
                    <div style={{ padding: "1rem"}}>
                        <div className='color-div' >
                            <div className='logo' style={{ backgroundColor: '#F4F4F4', width: '10%' }}></div>
                            <div style={{ backgroundColor: myProjects[currentIndex].colors[0], width: '40.9%' }} className='logo'></div>
                            <div style={{ backgroundColor: myProjects[currentIndex].colors[1], width: '14.7%' }} className='logo'></div>
                            <div style={{ backgroundColor: myProjects[currentIndex].colors[2], width: '14.7%' }} className='logo'></div>
                            <div style={{ backgroundColor: myProjects[currentIndex].colors[3], width: '15%' }} className='logo'></div>
                        </div>
                        <div style={{height: "70%", display: "flex", flexDirection: "column", paddingTop: ".5rem"}}>
                            <div className='project-description'>Role: {myProjects[currentIndex].role}</div>
                            <div className='project-description1'>{myProjects[currentIndex].description}</div>
                            <div className='button-div' style={{ display: "flex", justifyContent: "space-evenly" }}>
                                <Button color="primary"><a className='visit directly' style={{ color: '#004DC0', textDecoration: 'none' }} target="_blank" href={myProjects[currentIndex].URL[0]}><FontAwesomeIcon icon={faExternalLinkAlt} /> Visit Directly</a></Button>
                                <Button color="primary"><a className='visit github' style={{ color: '#004DC0', textDecoration: 'none' }} target="_blank" href={myProjects[currentIndex].URL[1]}><FontAwesomeIcon icon={faExternalLinkAlt} /> Visit Github</a></Button>
                            </div>
                        </div>
                    </div>
                </div>
                <div style={{ display: "flex", justifyContent: "space-evenly", alignItems: "center", padding: ".5rem" }}>
                    <ProjectButtons disabled={currentIndex === 0 ? true : false} onClick={() => setCurrentIndex(currentIndex - 1)}><FontAwesomeIcon className="fas fa-camera fa-3x" icon={faArrowCircleLeft} /></ProjectButtons>
                    <div>{currentIndex + 1} of {myProjects.length}</div>
                    <ProjectButtons disabled={currentIndex === 1 ? true : false} onClick={() => setCurrentIndex(currentIndex + 1)}><FontAwesomeIcon className="fas fa-camera fa-3x" icon={faArrowCircleRight} /></ProjectButtons>
                </div>
            </Modal>
        </div>
    );
}

export default Pros;
