import React, { useState } from 'react';
import Modal from 'react-modal';
import { projects } from '../../data';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowCircleRight, faArrowCircleLeft} from '@fortawesome/free-solid-svg-icons';

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)',
    height: "60vh",
    width: "80vw"
  }
};


const Pros = ({setIsOpen, modalIsOpen}) => {
    const [myProjects, setProjects] = useState(projects)
    const [currentIndex, setCurrentIndex]= useState(0)
    const closeModal = () => {
        setIsOpen(false);
    }

    console.log(myProjects)
    return (
      <div>

        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}

        >

        <h2 style={{ fontWeight: "bold" }}>{myProjects[currentIndex].name}</h2>
        <button style={{ postion: "relative", right: "0px"}}onClick={closeModal}>close</button>
        <div>{myProjects[currentIndex].subtitle}</div>
        <div>Role: {myProjects[currentIndex].role}</div>
        <div>{myProjects[currentIndex].description}</div>
        <div>
            <button disabled={currentIndex == 0 ? true : false} onClick={()=> setCurrentIndex(currentIndex - 1)}><FontAwesomeIcon icon={faArrowCircleLeft} /> </button>
            <button disabled={currentIndex == 1 ? true : false} onClick={() => setCurrentIndex(currentIndex + 1)}><FontAwesomeIcon icon={faArrowCircleRight} /></button>
        </div>
        </Modal>
      </div>
    );
}

export default Pros;

