import React from 'react';
import Modal from 'react-modal';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)'
    }
};


const Pros = ({ setIsOpen, modalIsOpen }) => {

    function closeModal() {
        setIsOpen(false);
    }

    return (
        <div>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                style={customStyles}
                contentLabel="Example Modal"
            >

                <div style={{ fontWeight: "bold" }}>{props.name}</div>
                <div>{props.subtitle}</div>
                <div style={{ display: "flex", justifyContent: "space-evenly", alignItems: "center" }}>
                    <img style={{ width: "55%", height: "100%" }} src={projects[0].photos[0]} />
                    <div style={{ display: "flex", flexDirection: "column", width: "40%" }}>
                        <div className='project-description'>Role: {projects[0].role}</div>
                        <p>{props.description}</p>
                        <div style={{ display: "flex", justifyContent: "space-evenly" }}>
                            <div className='vd-button' ><a className='visit directly' style={{ color: '#004DC0', textDecoration: 'none' }} target="_blank" href={projects[0].URL[0]}><FontAwesomeIcon icon={faExternalLinkAlt} /> Visit Directly</a></div>
                            <div className='gh-button' ><a className='visit github' style={{ color: 'white', textDecoration: 'none' }} target="_blank" href={projects[0].URL[1]}><FontAwesomeIcon icon={faExternalLinkAlt} /> Visit Github</a></div>
                        </div>
                    </div>
                </div>
                <section className='tech-section' style={{ paddingBottom: "3rem" }}>
                    <h4 className='techHeading'>Technologies used:</h4>
                    <div style={{ display: 'flex' }}>
                        {projects[0].tech.map(tech => (<div key={tech} className='tech'>{tech}</div>))}
                    </div>
                </section>
            </Modal>
        </div>
    );
}

export default Pros;
