import React, { useState } from 'react';
import Modal from 'react-modal';
import { projects } from '../../data';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowCircleRight, faArrowCircleLeft } from '@fortawesome/free-solid-svg-icons';
import Button from '@material-ui/core/Button';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        height: "60vh",
        width: "80vw",
        overflowX: "hidden", 
        display: "flex",
        flexDirection: "column"
    }
};


const Pros = ({ setIsOpen, modalIsOpen }) => {
    const [myProjects, setProjects] = useState(projects)
    const [currentIndex, setCurrentIndex] = useState(0)
    const closeModal = () => {
        setIsOpen(false);
    }

    console.log(myProjects)
    return (
        <div>

            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                style={customStyles}

            >
                {/* <Button color="secondary" style={{ position: "relative", left: "80vw" }} onClick={closeModal}>close</button> */}
                <div style={{height: "90vh"}}>
                    <h2 style={{ fontWeight: "bold" }}>{myProjects[currentIndex].name}</h2>
                    <div>{myProjects[currentIndex].subtitle}</div>
                    <div>Role: {myProjects[currentIndex].role}</div>
                    <div>{myProjects[currentIndex].description}</div>
                </div>
                <div style={{textAlign: "center"}}>
                    <Button disabled={currentIndex == 0 ? true : false} onClick={() => setCurrentIndex(currentIndex - 1)}><FontAwesomeIcon className="fas fa-camera fa-4x" icon={faArrowCircleLeft} /> </Button>
                    <Button disabled={currentIndex == 1 ? true : false} onClick={() => setCurrentIndex(currentIndex + 1)}><FontAwesomeIcon className="fas fa-camera fa-4x" icon={faArrowCircleRight} /></Button>
                </div>
            </Modal>
        </div>
    );
}

export default Pros;

